import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
class Square extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            value : null,
            class: 'square'
        }
    }
    render() {
      return (
        <button className={this.props.class} onClick={() => this.props.onClick()}>
          {this.props.value}
        </button>
      );
    }
}
class Board extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            squares: Array(9).fill(null),
            clickClass: Array(9).fill('square'),
        }
    }
    handleClick(i){
        const squares = this.state.squares.slice();
        const clickClass = this.state.clickClass.slice();
        squares[i] = 'X';
        clickClass[i] = 'square clicked';
        this.setState({squares: squares, clickClass: clickClass});
    }
    renderSquare(i, cls) {
        console.log(this.state)
      return <Square value={this.state.squares[i]} class={this.state.clickClass[i]} onClick={() => this.handleClick(i)}/>;
    }
    render() {
      const status = 'Next player: X';

      return (
        <div>
          <div className="status">{status}</div>
          <div className="board-row">
            {this.renderSquare(0,'square')}
            {this.renderSquare(1,'square')}
            {this.renderSquare(2,'square')}
          </div>
          <div className="board-row">
            {this.renderSquare(3,'square')}
            {this.renderSquare(4,'square')}
            {this.renderSquare(5,'square')}
          </div>
          <div className="board-row">
            {this.renderSquare(6,'square')}
            {this.renderSquare(7,'square')}
            {this.renderSquare(8,'square')}
          </div>
        </div>
      );
    }
}
class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
}
ReactDOM.render(
    <Game />,
    document.getElementById('root')
);
