import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
class Square extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            value : null,
            class: 'square'
        }
    }
    render() {
        return (
            <button className={this.props.class} onClick={() => this.props.onClick()}>
                {this.props.value}
            </button>
        );
    }
}
class Board extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            squares: Array(9).fill(null),
            clickClass: Array(9).fill('square'),
            disabled: Array(9).fill(false),
            xIsNext: true,
        }
    }
    handleClick(i){
        const squares = this.state.squares.slice();
        if(calculateWinner(squares)){
            return;
        }
        if(this.state.disabled[i]){
            alert("You can't click on this column, becuase it's already cliked by your opposit person")
            return;
        }
        const clickClass = this.state.clickClass.slice();
        const disabled = this.state.disabled.slice();
        squares[i] = this.state.xIsNext?'X':'O';
        clickClass[i] = this.state.xIsNext?'square xclicked':'square oclicked';
        disabled[i] = true;
        this.setState({squares: squares, clickClass: clickClass, xIsNext: !this.state.xIsNext, disabled: disabled});
    }
    renderSquare(i) {
        return <Square value={this.state.squares[i]} class={this.state.clickClass[i]} onClick={() => this.handleClick(i)} disabled={this.state.disabled[i]}/>;
    }
    render() {
        const winner = calculateWinner(this.state.squares);
        let status;
        if(winner)
            status = 'Congratulations the Winner is : '+ winner
        else
            status = 'Next player: '+(this.state.xIsNext?'X':'O');
        return (
            <div>
            <div className="status">{status}</div>
            <div className="board-row">
                {this.renderSquare(0)}
                {this.renderSquare(1)}
                {this.renderSquare(2)}
            </div>
            <div className="board-row">
                {this.renderSquare(3)}
                {this.renderSquare(4)}
                {this.renderSquare(5)}
            </div>
            <div className="board-row">
                {this.renderSquare(6)}
                {this.renderSquare(7)}
                {this.renderSquare(8)}
            </div>
            </div>
        );
    }
}
class Game extends React.Component {
    constructor(props){
        super(props);
    }
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
}
class GetPlayer extends React.Component{
    render(){
        return (
            <div className="player">
                <label>Player 1</label><input id="player1" type="text"></input>
                <label>Player 2</label><input id="player1" type="text"></input>
                <button onClick={renderGame()}>Continue</button>
            </div>
        );
    }
    renderGame(i) {
        return <Game value={this.state.squares[i]} class={this.state.clickClass[i]} onClick={() => this.handleClick(i)} disabled={this.state.disabled[i]}/>;
    }
}
ReactDOM.render(
    <GetPlayer />,
    document.getElementById('root')
);
function calculateWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
        return squares[a];
      }
    }
    return null;
}
