import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
class Square extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            value : null,
            class: 'square'
        }
    }
    render() {
        return (
            <button className={this.props.class} onClick={() => this.props.onClick()}>
                {this.props.value}
            </button>
        );
    }
}
class Board extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            squares: Array(9).fill(null),
            clickClass: Array(9).fill('square'),
            xIsNext: true,
        }
    }
    handleClick(i){
        const squares = this.state.squares.slice();
        const clickClass = this.state.clickClass.slice();
        squares[i] = this.state.xIsNext?'X':'O';
        clickClass[i] = this.state.xIsNext?'square xclicked':'square oclicked';
        this.setState({squares: squares, clickClass: clickClass, xIsNext: !this.state.xIsNext});
    }
    renderSquare(i, cls) {
        return <Square value={this.state.squares[i]} class={this.state.clickClass[i]} onClick={() => this.handleClick(i)}/>;
    }
    render() {
        const winner = calculateWinner(this.state.squares);
        console.log("winner:", winner)
        const status = 'Next player: '+(this.state.xIsNext?'X':'O');
        return (
            <div>
            <div className="status">{status}</div>
            <div className="board-row">
                {this.renderSquare(0,'square')}
                {this.renderSquare(1,'square')}
                {this.renderSquare(2,'square')}
            </div>
            <div className="board-row">
                {this.renderSquare(3,'square')}
                {this.renderSquare(4,'square')}
                {this.renderSquare(5,'square')}
            </div>
            <div className="board-row">
                {this.renderSquare(6,'square')}
                {this.renderSquare(7,'square')}
                {this.renderSquare(8,'square')}
            </div>
            </div>
        );
    }
}
class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
}
ReactDOM.render(
    <Game />,
    document.getElementById('root')
);
function calculateWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
        return squares[a];
      }
    }
    return null;
}
